-- Create

CREATE TABLE IF NOT EXISTS EMPLOYEES (
EMPLOYEE_ID INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL,
NAME NOT NULL,
EMAIL TEXT UNIQUE NOT NULL,
HIRE_DATE TEXT NOT NULL,
JOB_ID TEXT NOT NULL,
SALARY REAL NOT NULL
);


