-- Select

SELECT
*
FROM EMPLOYEES
ORDER BY EMPLOYEE_ID;

SELECT
COUNT(JOB_ID) AS 'Ppl. In Job',
JOB_ID AS 'Job (ID)'
FROM EMPLOYEES
GROUP BY JOB_ID;

SELECT
NAME AS 'Name',
JOB_ID as 'Job (ID)',
SALARY AS 'Salary',
HIRE_DATE AS 'Hired'
FROM EMPLOYEES
WHERE JOB_ID = 'DEV'
ORDER BY HIRE_DATE DESC;
